import axios from "axios";
import React from "react";
import { Button } from "@mui/material";


const baseURL = "https://pokeapi.co/api/v2/pokemon/";

function PokeImage(props) {
    const [post, setPost] = React.useState(null);

    React.useEffect(() => {
      axios.get(baseURL + props.pokename).then((response) => {
        setPost(response.data.sprites);
      });
    }, []);

    if (!post) return null;

    return (
        "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/889.png"
    );
}

export default PokeImage
